import React from 'react';
import { state, setState, useState, handleClick } from 'react'

const Layout = () => {
    return (
        <div style={{ display: 'flex' }}>
            <Sidebar />
            <MainContent />
        </div>
    );
};


const Sidebar = () => {
    const [isMapExpanded, setIsMapExpanded] = useState(false);

    return (
        <div style={{ width: '20%', height: '100%', backgroundColor: '#eee' }}>
            <Map isExpanded={isMapExpanded} />
            <ConversationLog />
            <button onClick={() => setIsMapExpanded(!isMapExpanded)}>View</button>
        </div>
    );
};


const Map = ({ isExpanded }) => {
    const mapStyle = {
        height: isExpanded ? '100%' : '50%',
    };

    return (
        <div style={mapStyle}>
            <h2 style={{ fontSize: '18px', textAlign: 'center' }}>Map</h2>
            <img src="/images/map.jpg" alt="Map" style={{ width: '100%' }} />
        </div>
    );
};


const ConversationLog = () => (
    <div style={{ height: '50%' }}>
        <h2 style={{ fontSize: '18px', textAlign: 'center' }}>Conversation Log</h2>
        <ul style={{ listStyleType: 'none', padding: 0 }}>
            <li style={{ margin: '10px 0', borderBottom: '1px solid #ccc' }}>Message 1</li>
            <li style={{ margin: '10px 0', borderBottom: '1px solid #ccc' }}>Message 2</li>
            <li style={{ margin: '10px 0', borderBottom: '1px solid #ccc' }}>Message 3</li>
        </ul>
    </div>
);


const MainContent = () => (
    <div style={{ width: '80%', height: '100%', backgroundColor: '#fff' }}>
        Main content goes here
    </div>
);

export default Layout;
